import React from 'react';
import { View, Image, StyleSheet, StatusBar, Text, TouchableOpacity } from 'react-native';
import Scan from './scan';


const HeaderOptions = (routeName, title, navigation) => {
    // let headerLeft = ''
    // if (routeName === 'Home') {
    //     headerLeft = () => (
    //         <View style={[globalStyle.headerHeight]}>
    //             <Image className="ml-2 w-10 h-10 rounded-full border-4"
    //                 source={require("../../assets/profile.jpg")} /></View>
    //     )
    // } else {
    //     headerLeft = () => (
    //         <TouchableOpacity style={[globalStyle.headerHeight]} onPress={() => navigation.goBack()}>
    //             <Ionicons name='arrow-back-outline' size={32} color='white' />
    //         </TouchableOpacity>
    //     )
    // }
    // let headerRight = () => (<View style={[globalStyle.headerHeight]}></View>)
    // if (routeName !== 'qr-scan') {
    //     headerRight = () => (
    //         <View style={[globalStyle.headerHeight]}>
    //             <TouchableOpacity style={[{ elevation: 5 }]} onPress={() => navigation.navigate('/qr-scan')}>
    //                 <Scan
    //                     style={[globalStyle.centerContainer, globalStyle.smallImg]} fill="white"></Scan>
    //             </TouchableOpacity>
    //         </View>
    //     )
    // }
    const headerComponent = ({ navigation, route, options, back }) => {
        return (
            <View className="flex flex-row mx-2" style={options.headerStyle}>
                <View className="flex h-30 basis-1/4" >
                    <Image className="ml-2 w-10 h-10 rounded-full border-4"
                        source={require("../../assets/profile.jpg")} />
                </View>
                <View className="flex h-30 basis-1/2 flex-center">
                    <Text className="text-white text-lg font-bold text-center">{options.title}</Text>
                </View>
                <View className="flex h-30 basis-1/4">
                    <TouchableOpacity className="flex items-end" onPress={() => navigation.navigate('/qr-scan')}>
                        <Scan
                            className="w-10 h-10" fill="white"></Scan>
                    </TouchableOpacity>
                </View>
            </View>
        )
    }
    return {
        title: title,
        cardStyle: { backgroundColor: 'transparent' },
        headerStyle: {
            height: 50,
            marginTop: StatusBar.currentHeight
        },
        header: headerComponent,
    }
}

const styles = StyleSheet.create({
    profileSize: {
        width: 40,
        height: 40,
        borderRadius: 40 / 2,
        borderWidth: 3,
        borderColor: "white",
    },
    headerHeight: {
        height: 50,
        backgroundColor: 'black'
    },
    container: {
        flex: 1,
        width: '100%',
        height: '100%',
        verticalAlign: 'top',
        alignContent: 'flex-start',
    },
    white: {
        color: 'white',

    }
})
export default HeaderOptions;
